mapper.insert:

    SELECT #{prodLgu} ||
    LPAD( NVL( TO_NUMBER( SUBSTR(MAX(PROD_ID), 5) ), 0) + 1, 6, '0')
    FROM PROD
    WHERE PROD_LGU = #{prodLgu}

